import React, { useState } from 'react';

function SignupModal({ setShowSignupModal, setShowLoginModal, handleSignup }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const onSubmit = (e) => {
    e.preventDefault();
    // In a real app, you would validate and create account here
    handleSignup({
      name: name,
      email: email
    });
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white dark:bg-gray-800 rounded-lg p-8 max-w-md w-full" style={{ backgroundColor: 'var(--bg-primary)' }}>
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-2xl font-bold">Create an Account</h3>
          <button onClick={() => setShowSignupModal(false)} className="text-gray-500 hover:text-gray-700">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
            </svg>
          </button>
        </div>
        <form onSubmit={onSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2" style={{ color: 'var(--text-secondary)' }}>Full Name</label>
            <input 
              type="text" 
              className="w-full px-3 py-2 border rounded-md" 
              style={{ backgroundColor: 'var(--bg-secondary)', color: 'var(--text-primary)', borderColor: 'var(--text-secondary)' }}
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2" style={{ color: 'var(--text-secondary)' }}>Email</label>
            <input 
              type="email" 
              className="w-full px-3 py-2 border rounded-md" 
              style={{ backgroundColor: 'var(--bg-secondary)', color: 'var(--text-primary)', borderColor: 'var(--text-secondary)' }}
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label className="block text-sm font-medium mb-2" style={{ color: 'var(--text-secondary)' }}>Password</label>
            <input 
              type="password" 
              className="w-full px-3 py-2 border rounded-md" 
              style={{ backgroundColor: 'var(--bg-secondary)', color: 'var(--text-primary)', borderColor: 'var(--text-secondary)' }}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button 
            type="submit"
            className="w-full py-2 px-4 rounded-md font-medium transition-all duration-300"
            style={{ backgroundColor: 'var(--accent)', color: 'white' }}
          >
            Sign Up
          </button>
          <div className="mt-4 text-center" style={{ color: 'var(--text-secondary)' }}>
            <p>Already have an account? <button onClick={() => {setShowSignupModal(false); setShowLoginModal(true);}} className="font-medium" style={{ color: 'var(--accent)' }}>Login</button></p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SignupModal;
