import React, { useState, useRef, useEffect } from 'react';
import UserProfile from './UserProfile';

function Navbar({ theme, toggleTheme, isLoggedIn, setShowLoginModal, setShowSignupModal, user, handleLogout }) {
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowProfileDropdown(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <nav className="px-4 py-5 flex justify-between items-center" style={{ backgroundColor: 'var(--bg-secondary)' }}>
      <div className="flex items-center">
        <svg className="w-8 h-8 mr-2" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M14.7519 4.03877C13.2366 2.52346 10.7634 2.52346 9.24806 4.03877L4.03807 9.24876C2.52276 10.7641 2.52276 13.2373 4.03807 14.7526L9.24806 19.9626C10.7634 21.4779 13.2366 21.4779 14.7519 19.9626L19.9619 14.7526C21.4772 13.2373 21.4772 10.7641 19.9619 9.24876L14.7519 4.03877Z" stroke="var(--accent)" strokeWidth="2"/>
          <path d="M12 8V16" stroke="var(--accent)" strokeWidth="2" strokeLinecap="round"/>
          <path d="M16 12L8 12" stroke="var(--accent)" strokeWidth="2" strokeLinecap="round"/>
        </svg>
        <span className="text-xl font-bold" style={{ color: 'var(--accent)' }}>StreamSync</span>
      </div>
      
      <div className="flex items-center">
        <button 
          onClick={toggleTheme}
          className="theme-toggle p-2 rounded-full mr-4 focus:outline-none"
          style={{ backgroundColor: 'var(--bg-secondary)' }}
        >
          {theme === 'light' ? (
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path d="M17.293 13.293A8 8 0 016.707 2.707a8.001 8.001 0 1010.586 10.586z"></path>
            </svg>
          ) : (
            <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
              <path fillRule="evenodd" d="M10 2a1 1 0 011 1v1a1 1 0 11-2 0V3a1 1 0 011-1zm4 8a4 4 0 11-8 0 4 4 0 018 0zm-.464 4.95l.707.707a1 1 0 001.414-1.414l-.707-.707a1 1 0 00-1.414 1.414zm2.12-10.607a1 1 0 010 1.414l-.706.707a1 1 0 11-1.414-1.414l.707-.707a1 1 0 011.414 0zM17 11a1 1 0 100-2h-1a1 1 0 100 2h1zm-7 4a1 1 0 011 1v1a1 1 0 11-2 0v-1a1 1 0 011-1zM5.05 6.464A1 1 0 106.465 5.05l-.708-.707a1 1 0 00-1.414 1.414l.707.707zm1.414 8.486l-.707.707a1 1 0 01-1.414-1.414l.707-.707a1 1 0 011.414 1.414zM4 11a1 1 0 100-2H3a1 1 0 000 2h1z" clipRule="evenodd"></path>
            </svg>
          )}
        </button>
        
        {!isLoggedIn ? (
          <>
            <button 
              onClick={() => setShowLoginModal(true)}
              className="px-4 py-2 rounded-md mr-2 transition-all duration-300"
              style={{ backgroundColor: 'transparent', color: 'var(--accent)', border: '1px solid var(--accent)' }}
            >
              Login
            </button>
            <button 
              onClick={() => setShowSignupModal(true)}
              className="px-4 py-2 rounded-md transition-all duration-300"
              style={{ backgroundColor: 'var(--accent)', color: 'white' }}
            >
              Sign Up
            </button>
          </>
        ) : (
          <div className="flex items-center">
            <button 
              className="px-6 py-2 rounded-md font-bold transition-all duration-300 mr-4"
              style={{ backgroundColor: 'var(--accent)', color: 'white' }}
            >
              WATCH NOW !!
            </button>
            
            <div className="relative" ref={dropdownRef}>
              <button 
                onClick={() => setShowProfileDropdown(!showProfileDropdown)}
                className="flex items-center focus:outline-none"
              >
                <div className="w-10 h-10 rounded-full bg-purple-500 flex items-center justify-center text-white font-bold">
                  {user?.name ? user.name.charAt(0).toUpperCase() : 'U'}
                </div>
              </button>
              
              {showProfileDropdown && (
                <UserProfile user={user} handleLogout={handleLogout} />
              )}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
