import React from 'react';

function CTASection({ isLoggedIn, setShowSignupModal }) {
  return (
    <section className="py-16 px-4 text-center" style={{ backgroundColor: 'var(--accent)', color: 'white' }}>
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold mb-6">Ready to Watch Together?</h2>
        <p className="text-xl mb-8 max-w-2xl mx-auto">Join thousands of friends who are already enjoying movies and shows together, no matter the distance.</p>
        <button 
          onClick={() => !isLoggedIn && setShowSignupModal(true)}
          className="px-8 py-3 rounded-full font-bold bg-white text-purple-700 hover:bg-gray-100 transition-all duration-300"
        >
          {isLoggedIn ? 'WATCH NOW !!' : 'Get Started for Free'}
        </button>
      </div>
    </section>
  );
}

export default CTASection;
